import {useCallback, useState} from "react";
import axios from "axios";
import SearchIcon from '@mui/icons-material/Search';
import {useRecoilState} from "recoil";
import {movieState} from "../../states/movieState";
import {searchKeywordState} from "../../states/searchKeywordState";
import AppLayout from "../../components/AppLayout";
import {InputWrapper, SearchForm, SearchWrapper} from "./styles";

const Search = () => {
  const [movies, setMovies] = useRecoilState(movieState);
  const [value, setValue] = useRecoilState(searchKeywordState);

  const onChangeValue = useCallback(async (e) => {
    setValue(e.target.value);
    try {
      const { data } = await axios.get('/api/search', {
        params: {
          query: value,
        },
      });
      setMovies(data);
    } catch (error) {
      console.error(error);
    }
  }, [value]);

  const onSubmitMovie = useCallback(async (e) => {
    e.preventDefault();
    try {
      if (value === "") {
        setMovies([]);
        setValue('');
      } else {
        const { data } = await axios.get('/api/search', {
          params: {
            query: value,
          },
        });
        setMovies(data);
      }
    } catch (error) {
      console.error(error);
    }
  }, [value]);

  console.log(movies);

  return (
    <AppLayout>
      <SearchWrapper>
        <SearchForm
          className="search-form"
          onSubmit={onSubmitMovie}
        >
          <p>
            영화 검색
          </p>
          <InputWrapper>
            <input
              type="text"
              onChange={onChangeValue}
              placeholder="키워드"
            />
          </InputWrapper>
        </SearchForm>
      </SearchWrapper>
    </AppLayout>
  );
};

export default Search;
