import {useRecoilState} from "recoil";
import {useCallback, useEffect, useState} from "react";
import axios from "axios";
import {Link, useParams} from "react-router-dom";
import Skeleton from '@mui/material/Skeleton';
import Stack from '@mui/material/Stack';
import AppLayout from "../../components/AppLayout";
import {BoxOfficeMovieCard, BoxOfficeWrapper, Header, SkeletonWrapper} from "./styles";
import {boxofficeState} from "../../states/boxofficeState";
import BoxOfficeCard from "../../components/BoxOfficeCard";
import {CardWrapper, MovieImg, MovieInfo, RankWrapper} from "../../components/BoxOfficeCard/styles";

const BoxOffice = () => {
  const [boxoffice, setBoxoffice] = useRecoilState(boxofficeState);
  const [isLoading, setIsLoading] = useState(true);
  const params = useParams();

  const fetchBoxOffice = useCallback(async () => {
    setIsLoading(true);
    try {
      const { data } = await axios.get('/api/boxoffice', {
        params: {
          period: params.period,
        },
      });
      setBoxoffice(data);
      setIsLoading(false);
    } catch (error) {
      let message = 'Unknown Error';
      if (error instanceof Error) message = error.message;
      console.log(message);
    }
  }, [params.period]);

  const onClickChangePeriod = useCallback(() => {
    fetchBoxOffice();
  }, []);

  useEffect(() => {
    fetchBoxOffice();
  }, [params.period]);

  const fakeArrayToSkeleton = Array.from({ length: 15 }, (v, i) => i);

  return (
    <AppLayout>
      <Header>
        <span>
          박스오피스
        </span>
        <div>
          <Link to="/boxoffice/weekly">
            <span>주간</span>
          </Link>
          <Link to="/boxoffice/monthly">
            <span>월간</span>
          </Link>
          <Link to="/boxoffice/yearly">
            <span>년간</span>
          </Link>
        </div>
      </Header>
      <BoxOfficeWrapper>
        {
          !isLoading
            ? (
              <BoxOfficeMovieCard>
                <SkeletonWrapper>
                  <Stack spacing={1}>
                    {
                      fakeArrayToSkeleton.map((v, i) => {
                        return (
                          <CardWrapper>
                            <MovieImg>
                              <Skeleton variant="rounded" width={240} height={340} />
                            </MovieImg>
                            <MovieInfo>
                              <p>
                                <Skeleton variant="rounded" width={100} height={14} />
                              </p>
                              <p>
                                <Skeleton variant="rounded" width={240} height={14} />
                              </p>
                            </MovieInfo>
                          </CardWrapper>
                        );
                      })
                    }
                  </Stack>
                </SkeletonWrapper>
              </BoxOfficeMovieCard>
            )
            : (
              <BoxOfficeMovieCard>
                {
                  boxoffice.map((data, i) => {
                    return (
                      <BoxOfficeCard key={data.title} data={data} />
                    );
                  })
                }
              </BoxOfficeMovieCard>
            )
        }
      </BoxOfficeWrapper>
    </AppLayout>
  );
};

export default BoxOffice;
