import {useCallback, useEffect, useRef} from "react";
import {Link} from "react-router-dom";
import {Background, MenuWrapper} from "./styles";

const MobileMenu = ({ setShowMobileMenu }) => {
  const onClickCloseMenu = useCallback((e) => {
    if (e.target === e.currentTarget) {
      setShowMobileMenu(false);
    }
  }, []);

  return (
    <Background onClick={onClickCloseMenu}>
      <MenuWrapper>
        <h1>
          Mucvies
        </h1>
        <ul>
          <li>
            <Link to="/search">
              영화검색
            </Link>
          </li>
          <li>
            <Link to="/boxoffice">
              박스오피스
            </Link>
          </li>
        </ul>
      </MenuWrapper>
    </Background>
  );
};

export default MobileMenu;
