import {useCallback, useEffect, useRef} from "react";
import {Background, MenuWrapper} from "./styles";

const MobileMenu = ({ setShowMobileMenu }) => {
  const MenuRef = useRef();

  const onClickCloseMenu = useCallback((e) => {
    if (MenuRef.current && !MenuRef.current.contains(e.target)) {
      setShowMobileMenu(false);
    } else {
      setShowMobileMenu(true);
    }
  }, [MenuRef]);

  useEffect(() => {
    document.addEventListener("mousedown", onClickCloseMenu);
    return () => {
      document.removeEventListener("mousedown", onClickCloseMenu);
    };
  }, []);

  return (
    <Background onClick={onClickCloseMenu} ref={MenuRef}>
      <MenuWrapper>
        123
      </MenuWrapper>
    </Background>
  );
};

export default MobileMenu;
