import styled from "@emotion/styled";
import {BACKGROUND_COLOR, BUTTON_HOVER} from "../../styles/common";

export const Header = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  margin-bottom: 70px;
  span {
    padding-top: 4px;
    font-size: 22px;
    font-weight: bold;
  }
  div {
    margin-left: 40px;
    padding-top: 4px;
    a {
      padding: 10px 20px;
      background-color: ${BACKGROUND_COLOR};
      border-radius: 14px;
      transition: background-color 0.16s ease-in;
      &:hover {
        background-color: ${BUTTON_HOVER};
      }
      span {
        opacity: 0.5;
        font-size: 16px;
        font-weight: 500;
      }
    }
  }
  @media (max-width: 700px) {
    flex-direction: column;
    margin-bottom: 70px;
    span {
      margin-bottom: 20px;
    }
    div {
      margin: 0;
      a {
        padding: 14px 24px;
        font-size: 18px;
      }
    }
  }
`;

export const BoxOfficeWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

export const BoxOfficeMovieCard = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 100%;
`;

export const SkeletonWrapper = styled.div`
  width: 100%;
  span {
    background-color: rgba(255, 255, 255, 0.06);
  }
`;
