import styled from "@emotion/styled";
import {BACKGROUND_COLOR, BUTTON_HOVER} from "../../styles/common";

export const Background = styled.div`
  @media (max-width: 700px) {
    flex-direction: column;
    padding-top: 0;
    & > div {
      padding: 0 20px;
    }
  }
  display: flex;
  max-width: 1600px;
  margin: 0 auto;
  padding-top: 70px;
`;

export const LeftMenu = styled.div`
  width: 240px;
  padding: 0 20px;
  margin-right: 30px;
  h1 {
    font-size: 28px;
    margin-bottom: 70px;
    padding: 0 20px;
  }
  ul {
    li {
      a {
        display: flex;
        background-color: ${BACKGROUND_COLOR};
        font-weight: 500;
        border-radius: 14px;
        align-items: center;
        padding: 20px;
        width: 100%;
        opacity: 0.5;
        transition: background-color, opacity 0.16s ease-in;
        svg {
          margin-right: 14px;
        }
        &:hover {
          background-color: ${BUTTON_HOVER};
          opacity: 1;
        }
      }
    }
  }
`;

// Mobile Components

export const MobileTopBar = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  height: 60px;
  margin-bottom: 20px;
`;

export const MenuBar = styled.div`
  padding: 0 20px;
`;
