import styled from "@emotion/styled";

export const Background = styled.div`
  display: flex;
  max-width: 1600px;
  margin: 0 auto;
  padding-top: 70px;
`;

export const LeftMenu = styled.div`
  width: 240px;
  padding: 0 20px;
  margin-right: 30px;
  h1 {
    font-size: 28px;
    margin-bottom: 70px;
    padding: 0 20px;
  }
  ul {
    li {
      a {
        display: flex;
        text-align: center;
        padding: 20px;
        width: 100%;
        opacity: 0.5;
      }
    }
  }
`;
