import styled from "@emotion/styled";
import {GRAY_COLOR, WHITE_COLOR} from "../../styles/common";

export const Header = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 70px;
  span:nth-of-type(1) {
    font-size: 22px;
    padding-top: 4px;
    font-weight: bold;
    margin-right: 10px;
  }
`;

export const Content = styled.div`
  display: flex;
  div:nth-of-type(1) {
    margin-right: 40px;
    img {
      width: 240px;
      border-radius: 14px;
    }
  }
  div:nth-of-type(2) {
    margin-right: 70px;
  }
`;

export const MovieTitle = styled.div`
  margin-bottom: 40px;
  p:nth-of-type(1) {
    font-size: 22px;
    font-weight: bold;
    margin-bottom: 10px;
  }
  p:nth-of-type(2) {
    font-size: 18px;
    color: ${GRAY_COLOR};
  }
`;

export const MovieInfo = styled.div`
  display: flex;
  flex-direction: column;
  div {
    color: ${GRAY_COLOR};
    span {
      color: ${WHITE_COLOR};
      margin-left: 10px;
    }
  }
`;
