import styled from "@emotion/styled";
import {RED_COLOR} from "../../styles/common";

export const Bubble = styled.div`
  position: absolute;
  top: -50px;
  right: 0;
`;

export const BubbleWrapper = styled.div`
  position: relative;
  padding: 14px 20px;
  border-radius: 14px;
  background-color: ${RED_COLOR};
  font-size: 14px;
`;

export const Triangle = styled.div`
  background-color: ${RED_COLOR};
`;
