import styled from "@emotion/styled";
import {RED_COLOR} from "../../styles/common";

export const BubbleWrapper = styled.div`
  position: absolute;
  padding: 20px;
  border-radius: 14px;
  background-color: ${RED_COLOR};
  font-size: 14px;
  top: 0;
`;
