import {InputWrapper} from "../../routes/Search/styles";

const SearchForm = () => {
  const onChangeValue = useCallback(async (e) => {
    setValue(e.target.value);
    try {
      const { data } = await axios.get('/api/search', {
        params: {
          query: value,
        },
      });
      setMovies(data);
    } catch (error) {
      console.error(error);
    }
  }, [value]);

  return (
    <>
      <p>
        영화 검색
      </p>
      <InputWrapper>
        <input
          type="text"
          onChange={onChangeValue}
          placeholder="키워드"
        />
        <SearchIcon />
      </InputWrapper>
    </>
  );
};

export default memo(SearchForm);
