import {useRecoilState, useRecoilValue} from "recoil";
import {useState} from "react";
import {movieState} from "../../states/movieState";
import AppLayout from "../../components/AppLayout";
import {InputWrapper, ResultMovieCard, SearchResult, SearchResultWrapper, SearchWrapper} from "./styles";
import SearchForm from "../../components/SearchForm";
import {searchKeywordState} from "../../states/searchKeywordState";
import MovieCard from "../../components/MovieCard";
import Skeleton from '@mui/material/Skeleton';
import Stack from '@mui/material/Stack';

const Search = () => {
  const [movies, setMovies] = useRecoilState(movieState);
  const searchKeyword = useRecoilValue(searchKeywordState);

  const [keyword, setKeyword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  return (
    <AppLayout>
      <SearchWrapper>
        <SearchForm
          setMovies={setMovies}
          setKeyword={setKeyword}
          setIsLoading={setIsLoading}
        />
        <SearchResult>
          {
            movies.length === 0
              ? <p>영화를 검색해보세요</p>
              : isLoading
                ? (
                  <SearchResultWrapper>
                    123
                  </SearchResultWrapper>
                )
                : (
                  <SearchResultWrapper>
                    <p>
                      {`"${keyword}"에 대한 검색 결과입니다.`}
                    </p>
                    <ResultMovieCard>
                      {
                        movies?.map((item, idx) => {
                          return (
                            <MovieCard key={item.title} item={item} />
                          );
                        })
                      }
                    </ResultMovieCard>
                  </SearchResultWrapper>
                )
          }
        </SearchResult>
      </SearchWrapper>
    </AppLayout>
  );
};

export default Search;
