import styled from "@emotion/styled";
import {GRAY_COLOR} from "../../styles/common";

export const Header = styled.div`
  position: relative;
  width: 100%;
  margin-bottom: 70px;
  span:nth-of-type(1) {
    font-size: 22px;
    padding-top: 4px;
    font-weight: bold;
    margin-right: 10px;
  }
  span:nth-of-type(2) {
    color: ${GRAY_COLOR};
  }
`;

export const Content = styled.div`
  display: flex;
`;
