import styled from "@emotion/styled";
import {INPUT_COLOR} from "../../styles/common";

export const SearchWrapper = styled.div`
  display: flex;
  width: 300px;
  padding: 0 20px;
`;

export const SearchForm = styled.form`
  p {
    font-size: 24px;
    font-weight: bold;
    opacity: 0.5;
    padding-top: 6px;
    margin-bottom: 70px;
  }
  input {
    padding: 22px 24px;
    border-radius: 14px;
    background-color: ${INPUT_COLOR};
  }
`;
