import {useCallback, useEffect, useState} from "react";
import {useParams, Link} from "react-router-dom";
import axios from "axios";
import AppLayout from "../../components/AppLayout";
import watchaLogo from "../../asset/watcha_logo.svg";
import tvingLogo from "../../asset/tving_logo.svg";
import waveLogo from "../../asset/wave_logo.svg";
import {Content, Header, MovieInfo, MovieTitle} from "./styles";

const Movie = () => {
  const params = useParams();
  const [movieData, setMovieData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  console.log(movieData);

  const fetchBoxOffice = useCallback(async () => {
    setIsLoading(true);
    try {
      const { data } = await axios.get('/api/movie', {
        params: {
          movieCode: params.code,
        },
      });
      setMovieData(data);
      setIsLoading(false);
    } catch (error) {
      let message = 'Unknown Error';
      if (error instanceof Error) message = error.message;
      console.log(message);
    }
  }, [params.code]);

  useEffect(() => {
    fetchBoxOffice();
  }, [params.code]);

  return (
    <AppLayout>
      <Header>
        <span>
          영화정보
        </span>
      </Header>
      <Content>
        <div>
          <img src={movieData.image} alt="movie_poster" />
        </div>
        <div>
          <MovieTitle>
            <p>{ movieData.title }</p>
            <p>{ movieData.subTitle }</p>
          </MovieTitle>
          <MovieInfo>
            <div>
              개봉일
              <span>{ movieData.release }</span>
            </div>
            <div>
              장르
              <span>{ movieData.genre }</span>
            </div>
            <div>
              국가
              <span>{ movieData.country }</span>
            </div>
            <div>
              등급
              <span>{ movieData.rank }</span>
            </div>
            <div>
              러닝타임
              <span>{ movieData.runningTime }</span>
            </div>
            <div>
              평점
              <span>{ movieData.rating }</span>
            </div>
            <div>
              관객수
              <span>{ movieData.attendance }</span>
            </div>
          </MovieInfo>
        </div>
        <div>
          123
        </div>
      </Content>
    </AppLayout>
  );
};

export default Movie;
