import {useCallback, useEffect, useState} from "react";
import axios from "axios";
import {useRecoilState} from "recoil";
import AppLayout from "../../components/AppLayout";
import {ottState} from "../../states/ottState";
import {Header, OttMovieCard, OttSection} from "./styles";
import OttCard from "../../components/OttCard";
import {BoxOfficeMovieCard, SkeletonWrapper} from "../BoxOffice/styles";
import Stack from "@mui/material/Stack";
import {CardWrapper, MovieImg, MovieInfo} from "../../components/BoxOfficeCard/styles";
import Skeleton from "@mui/material/Skeleton";

const OTT = () => {
  const [ott, setOtt] = useRecoilState(ottState);
  const [isLoading, setIsLoading] = useState(true);

  const fetchRanking = useCallback(async () => {
    setIsLoading(true);
    try {
      const { data } = await axios.get('/api/ott');
      setOtt(data);
      setIsLoading(false);
    } catch (error) {
      let message = 'Unknown Error';
      if (error instanceof Error) message = error.message;
      console.log(message);
    }
  }, []);

  useEffect(() => {
    fetchRanking();
  }, []);

  console.log(ott);

  return (
    <AppLayout>
      {
        !isLoading
          ? (
            <SkeletonWrapper>
              <Stack spacing={1}>
                <BoxOfficeMovieCard>
                  {
                    fakeArrayToSkeleton.map((v, i) => {
                      return (
                        <CardWrapper key={v}>
                          <MovieImg>
                            <Skeleton variant="rounded" width="100%" height={300} />
                          </MovieImg>
                          <MovieInfo>
                            <p>
                              <Skeleton variant="rounded" width={100} height={14} />
                            </p>
                            <p>
                              <Skeleton variant="rounded" width="100%" height={14} />
                            </p>
                          </MovieInfo>
                        </CardWrapper>
                      );
                    })
                  }
                </BoxOfficeMovieCard>
              </Stack>
            </SkeletonWrapper>
          )
          : (
            ott.map((data, i) => {
              return (
                <OttSection key={data.movie[i].title}>
                  <Header>
                    { data.title }
                  </Header>
                  <OttMovieCard>
                    {
                      data.movie.map((movie, idx) => {
                        return (
                          <OttCard data={movie} />
                        );
                      })
                    }
                  </OttMovieCard>
                </OttSection>
              );
            })
          )
      }
    </AppLayout>
  );
};

export default OTT;
