import styled from "@emotion/styled";
import {BACKGROUND_COLOR} from "../../styles/common";

export const Header = styled.p`
  display: flex;
  align-items: center;
  width: 100%;
  margin-bottom: 70px;
  span {
    padding-top: 4px;
    font-size: 22px;
    font-weight: bold;
  }
  div {
    margin-left: 40px;
    padding-top: 4px;
    button {
      padding: 10px 20px;
      background-color: ${BACKGROUND_COLOR};
      span {
        opacity: 0.5;
        font-size: 16px;
        font-weight: 500;
      }
    }
  }
`;

export const BoxOfficeWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

export const BoxOfficeMovieCard = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 100%;
`;

export const periodButtonWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
