import styled from "@emotion/styled";

export const OttSection = styled.div`
  margin-bottom: 70px;
`;

export const Header = styled.h1`
  font-size: 22px;
  margin-bottom: 70px;
  @media (max-width: 700px) {
    width: 100%;
    text-align: center;
  }
`;

export const OttMovieCard = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 100%;
`;
